input5 = ""


length = input5.length
currentNumber = 5
length.times do |a|
	direction = input5[a].to_s
	if direction == "R"
		if currentNumber % 3 != 0
			currentNumber += 1
		end
	elsif direction == "L"
		if currentNumber % 3 != 1
			currentNumber -= 1
